/* Versão 2012.04.20 */
CREATE TABLE anos
(
	id 							INTEGER 			NOT NULL PRIMARY KEY AUTO_INCREMENT,
	designacao 					VARCHAR(128) 		NOT NULL,
    data_disponivel_de			DATETIME 			NOT NULL,
    data_disponivel_ate			DATETIME 			NOT NULL,
	valor_a_pagar				DECIMAL(8,2)		NOT NULL,
	activo						BOOLEAN				NOT NULL,
	opcao_1_titulo				VARCHAR(128)		NOT NULL,
	opcao_1_valor_a_pagar		DECIMAL(8,2)		NOT NULL,
	opcao_1_activo				BOOLEAN				NOT NULL,
	opcao_2_titulo				VARCHAR(128)		NOT NULL,
	opcao_2_valor_a_pagar		DECIMAL(8,2)		NOT NULL,
	opcao_2_activo				BOOLEAN				NOT NULL,
	opcao_3_titulo				VARCHAR(128)		NOT NULL,
	opcao_3_valor_a_pagar		DECIMAL(8,2)		NOT NULL,
	opcao_3_activo				BOOLEAN				NOT NULL,
	id_tree 					INTEGER 			NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;




CREATE TABLE IF NOT EXISTS `tree` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `parent_id` bigint(20) unsigned NOT NULL,
  `position` bigint(20) unsigned NOT NULL,
  `left` bigint(20) unsigned NOT NULL,
  `right` bigint(20) unsigned NOT NULL,
  `level` bigint(20) unsigned NOT NULL,
  `title` text CHARACTER SET utf8 COLLATE utf8_unicode_ci,
  `type` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=713;







CREATE TABLE IF NOT EXISTS `escolas`(
	`id_escola`          	INT        		NOT NULL AUTO_INCREMENT,
	`escola_nome`				varchar(200) 	NOT NULL,
	`escola_email`				varchar(200) 	NOT NULL,
	`escola_url`				varchar(200) 	NOT NULL,
	`cabecalho`         		text 	NOT NULL,
	`coluna1`         			text 	NOT NULL,
	`coluna2`         			text 	NOT NULL,
	`coluna3`         			text 	NOT NULL,
	`rodape`         			text 	NOT NULL,
	`logotipo`         			varchar(200) 	NOT NULL,
	`email_remetente`			varchar(200) 	NOT NULL,
	`email_destinatario`		varchar(200) 	NOT NULL,
	`url_matriculas`			varchar(200) 	NOT NULL,
	`observacoes`         		text 	NOT NULL,
  PRIMARY KEY           (`id_escola`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;



